@import url('https://fonts.googleapis.com/css2?family=Playfair+Display&display=swap');

$break-small: 500px;
$break-large: 1200px;

$primary-stack:  'Playfair Display', serif;
$secondary-stack:  Helvetica, sans-serif;

$primary-color: #333;

*{
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

h1,h2 {
    font: 100% $primary-stack;
}

a{
    color: #0a0a0a;
    text-decoration: none;
}

::-webkit-scrollbar {
    width: 4px;
}
  
::-webkit-scrollbar-track {
    background: transparent;
}
  
::-webkit-scrollbar-thumb {
    background: rgba(0,0,0,.5);
    border-radius: 2px;
}
  
::-webkit-scrollbar-thumb:hover {
    background: #aaa;
}


h1{
    @media screen and (max-width: $break-small) {
        font-size: 28px;
    }
    
    @media screen and (min-width: $break-large) {
        font-size: 48px;
    }

    margin: 30px 0;
}

p{
    @media screen and (max-width: $break-small) {
        font-size: 18px;
    }
    
    @media screen and (min-width: $break-large) {
        font-size: 28px;
    }

    margin: 24px 0;
}

