@import './base.scss';

nav.overlay-nav{
    height: 150px;
    position: fixed;
    z-index: 1;
    background: #e8dfd8;
    width: 100%;

    #menu-button{
        width: 128px;
        height: 128px;
       
        background-color: transparent;
        border: none;
        outline: none;
        
        cursor: pointer;

        top: 10px;
        right: 10px;
        position: fixed;
        z-index: 2;
    }

    #title-card{
        width: 96px;
        height: 96px;

        top: 30px;
        left: 30px;
        position: fixed;
        z-index: 2;

        background-color: #0a0a0a;

        display: flex;
        align-items: center; 

        span{
            font: 100% $primary-stack;

            font-size: 18px;
            color: white;
            margin-left: 10px;
        }
    }

    .overlay{
        width: 100%;
        height: 100%;
        position: fixed;
        left: 0;
        top: -100%;
        z-index: 1;
        background-color: #0a0a0a;
       
        display: flex;
        align-items: center;

        .nav-links{ 
            margin-left: 8%;
