                @media screen and (min-width: $break-large) {
                    font-size: 120px;
                }

                letter-spacing: 0.5px;
                transition: letter-spacing 250ms;            
            }

            a:hover{
                letter-spacing: 10px;
                transition: letter-spacing 250ms;            
            }

            .side-links{
