                    @media screen and (max-width: $break-small) {
                        font-size: 18px;
                    }
                
                    @media screen and (min-width: $break-large) {
                        font-size: 48px;
                    }

                    letter-spacing: 0.5px;
                    transition: letter-spacing 250ms; 
                }
            }
        }
    }
}

.home, .about, .project{
    height: 100vh;
    position: absolute;
    width: 100%;
}

.home-content, .about-content, .project-content{   
    .slide{
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;

        padding: 20px;

        @media screen and (min-width: $break-large) {
            padding: 100px;
        }
    }
    
    #slide-1{
        height: 800px;
        background-color: #e8dfd8;

        .row {
            width: 100%;
            
            .col:nth-child(1){
                @media screen and (max-width: $break-small) {
