        h1{
            margin: 30px 0;   
            font-size: 24px;
            
            @media screen and (min-width: $break-large) {
                font-size: 48px;
            }
        }

        h2{
            font-size: 18px;
            
            @media screen and (min-width: $break-large) {
                font-size: 28px;
            }
        }
    }
}


